defmodule ComponentPreviewer.IconLive do
  @moduledoc false
  use ComponentPreviewer, :live_view

  @impl true
  def render(assigns) do
    ~H"""
    <.header>
      &lt;.icon&gt;<:actions>
        <.doc_url page="Phlegethon.Components.Core.html#icon/1" />
      </:actions>
    </.header>

    <div class="flex flex-wrap gap-2 justify-start items-end">
      <%= for name <- get_prop_value_opts(Phlegethon.Components.Core, :icon, :attrs, :name) do %>
        <.tooltip
          id={"#{name}--tooltip"}
          icon_name={name}
          icon_class={get_class(name)}
          tooltip={name}
        />
      <% end %>
    </div>
    """
  end

  @impl true
  def mount(_params, _session, socket) do
    {:ok, socket |> assign(:page_title, "<.icon>")}
  end

  defp get_class(name) do
    cond do
      String.ends_with?(name, "-mini") -> "h-5 w-5"
      true -> "h-6 w-6"
    end
  end
end

